/* Reset e Base */
* {
  box-sizing: border-box;
}

/* Container Principal */
.chat-window {
  background: var(--chat--color-background);
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.5),
              0 12px 24px -8px rgba(0, 0, 0, 0.3);
  border: 1px solid var(--chat--color-border);
  overflow: hidden;
}

/* Header Elegante */
.chat-header {
  background: var(--chat--color-background);
  border-bottom: 1px solid var(--chat--color-border);
}

.chat-header h1,
.chat-header .chat-heading {
  font-weight: 600;
  letter-spacing: -0.02em;
  color: var(--chat--color-text-primary);
}

.chat-header .chat-subtitle {
  color: var(--chat--color-text-secondary);
  font-weight: 400;
}

/* Ãrea de Mensagens */
.chat-messages-list {
  background: var(--chat--color-background);
  scroll-behavior: smooth;
}

/* Estilo das Mensagens */
.chat-message {
  max-width: 85%;
  animation: messageSlideIn 0.3s ease-out;
}

@keyframes messageSlideIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Mensagens do Bot */
.chat-message-bot {
  background: var(--chat--color-surface);
  border-radius: 1.25rem 1.25rem 1.25rem 0.375rem;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
  border: 1px solid var(--chat--color-border-light);
}

.chat-message-bot p {
  color: var(--chat--color-text-primary);
  line-height: 1.65;
}

/* Mensagens do UsuÃ¡rio */
.chat-message-user {
  background: linear-gradient(135deg, var(--chat--color-primary) 0%, var(--chat--color-primary-shade-100) 100%);
  border-radius: 1.25rem 1.25rem 0.375rem 1.25rem;
  box-shadow: 0 2px 12px rgba(217, 119, 87, 0.3);
  border: none;
}

.chat-message-user p {
  color: #FFFFFF;
}

/* Ãrea de Input Moderna */
.chat-input {
  background: var(--chat--color-background);
  padding: 1rem 1.25rem 1.25rem;
  border-top: 1px solid var(--chat--color-border);
}

.chat-input-wrapper,
.chat-inputs {
  background: var(--chat--color-surface);
  border: 1px solid var(--chat--color-border);
  border-radius: var(--chat--border-radius-lg);
  transition: all var(--chat--transition-duration) ease;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.15);
}

.chat-input-wrapper:focus-within,
.chat-inputs:focus-within {
  border-color: var(--chat--color-primary);
  box-shadow: 0 0 0 3px rgba(217, 119, 87, 0.15),
              0 2px 8px rgba(0, 0, 0, 0.15);
}

/* Textarea */
.chat-input textarea,
.chat-textarea {
  background: transparent;
  border: none;
  resize: none;
  font-size: var(--chat--input--font-size);
  line-height: 1.5;
  color: var(--chat--color-text-primary);
  padding: 0.875rem 1rem;
}

.chat-input textarea::placeholder,
.chat-textarea::placeholder {
  color: var(--chat--color-text-muted);
  opacity: 1;
}

.chat-input textarea:focus,
.chat-textarea:focus {
  outline: none;
  box-shadow: none;
}

/* BotÃ£o de Enviar */
.chat-input-send-button,
.chat-send-button {
  background: var(--chat--color-primary);
  color: #FFFFFF;
  border: none;
  border-radius: 50%;
  width: 36px;
  height: 36px;
  min-width: 36px;
  min-height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all var(--chat--transition-duration) ease;
  margin: 0.5rem;
}

.chat-input-send-button:hover,
.chat-send-button:hover {
  background: var(--chat--color-primary-shade-50);
  transform: scale(1.05);
  box-shadow: 0 4px 12px rgba(217, 119, 87, 0.4);
}

.chat-input-send-button:active,
.chat-send-button:active {
  transform: scale(0.95);
}

.chat-input-send-button:disabled,
.chat-send-button:disabled {
