}

/* Toggle Button (Flutuante) */
.chat-toggle-button,
.chat-toggle {
  background: linear-gradient(135deg, var(--chat--color-primary) 0%, var(--chat--color-primary-shade-100) 100%);
  box-shadow: 0 4px 20px rgba(217, 119, 87, 0.4),
              0 2px 8px rgba(0, 0, 0, 0.2);
  transition: all var(--chat--transition-duration) ease;
}

.chat-toggle-button:hover,
.chat-toggle:hover {
  transform: scale(1.08);
  box-shadow: 0 6px 24px rgba(217, 119, 87, 0.5),
