.chat-loading::after {
  content: '';
  width: 24px;
  height: 24px;
  border: 2px solid var(--chat--color-border);
  border-top-color: var(--chat--color-primary);
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

/* Mensagens de Erro */
.chat-error {
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid rgba(239, 68, 68, 0.3);
  color: #F87171;
  border-radius: var(--chat--border-radius-sm);
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
}

/* Mensagem de Boas-vindas */
.chat-welcome-message {
  text-align: center;
  padding: 2rem;
  color: var(--chat--color-text-secondary);
}

.chat-welcome-message h2 {
  color: var(--chat--color-text-primary);
  font-size: 1.25rem;
  font-weight: 600;
  margin-bottom: 0.5rem;
}

/* SugestÃµes de Prompts */
.chat-suggestions {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  padding: 1rem;
  justify-content: center;
}

.chat-suggestion-button {
  background: var(--chat--color-surface);
  border: 1px solid var(--chat--color-border);
  border-radius: 1rem;
  padding: 0.5rem 1rem;
  font-size: 0.875rem;
  color: var(--chat--color-text-secondary);
  cursor: pointer;
  transition: all var(--chat--transition-duration) ease;
}

.chat-suggestion-button:hover {
  border-color: var(--chat--color-primary);
  color: var(--chat--color-primary);
  background: var(--chat--color-background-hover);
}

/* Listas nas mensagens */
.chat-message ul,
.chat-message ol {
  margin: 0.5rem 0;
  padding-left: 1.5rem;
  color: var(--chat--color-text-primary);
}

.chat-message li {
  margin-bottom: 0.25rem;
  line-height: 1.6;
}

/* CitaÃ§Ãµes/Blockquotes */
.chat-message blockquote {
  border-left: 3px solid var(--chat--color-primary);
  margin: 0.75rem 0;
  padding: 0.5rem 1rem;
  background: rgba(217, 119, 87, 0.1);
  border-radius: 0 0.5rem 0.5rem 0;
  color: var(--chat--color-text-secondary);
}

/* Separadores */
.chat-message hr {
  border: none;
  border-top: 1px solid var(--chat--color-border);
  margin: 1rem 0;
}

/* Tabelas */
.chat-message table {
  width: 100%;
  border-collapse: collapse;
  margin: 0.75rem 0;
  font-size: 0.875rem;
}

.chat-message th,
.chat-message td {
  border: 1px solid var(--chat--color-border);
  padding: 0.5rem 0.75rem;
  text-align: left;
}

.chat-message th {
  background: var(--chat--color-surface-light);
  color: var(--chat--color-text-primary);
  font-weight: 600;
}

.chat-message td {
  color: var(--chat--color-text-secondary);
}

/* Imagens nas mensagens */
.chat-message img {
  max-width: 100%;
  border-radius: 0.75rem;
  margin: 0.5rem 0;
}

/* SeleÃ§Ã£o de texto */
::selection {
  background: rgba(217, 119, 87, 0.3);
  color: var(--chat--color-text-primary);
}

/* Focus visible para acessibilidade */
*:focus-visible {
  outline: 2px solid var(--chat--color-primary);
  outline-offset: 2px;
}

/* TransiÃ§Ã£o suave ao abrir/fechar chat */
.chat-window {
  animation: chatOpen 0.3s ease-out;
}

@keyframes chatOpen {
  from {
    opacity: 0;
    transform: scale(0.95) translateY(10px);
  }
  to {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
}
