// n8n Function node -> apenas concatena e retorna markdown unificado
let mdList = [];

for (const it of items) {
  const j = it.json || {};
  if (typeof j.markdown === 'string' && j.markdown.trim()) mdList.push(j.markdown);
  if (Array.isArray(j.pages)) {
    for (const p of j.pages) if (p && p.markdown) mdList.push(p.markdown);
  }
  if (j.document && Array.isArray(j.document.pages)) {
    for (const p of j.document.pages) if (p && p.markdown) mdList.push(p.markdown);
  }
}

return [{
  json: {
    markdown: mdList.join('\n')
  }
}];
